import { useState } from 'react';
import axios from 'axios';

const baseUrl = 'https://www.metaweather.com/api/location';
const crossDomain = 'https://the-ultimate-api-challenge.herokuapp.com';
const requestUrl = `${crossDomain}/${baseUrl}`;

const useForecast = () => {
    const [isError, setError] = useState(false);
    const [isLoading, setLoading] = useState(false);
    const [forecast, setForecast] = useState(null);

    const getWoeid = async (location) => {
        const { data } = await axios(`${requestUrl}/search`, {params: { query: location }});

        if (!data || data.length === 0) {
            setError('No such location');
            setLoading(false);
            return;
        }

        return data[0];
    };

    const getForecastData = async (woeid) => {
        const {data} = await axios(`${requestUrl}/${woeid}`);
        if (!data || data.length === 0) {
            setError('Something went wrong');
            setLoading(false);
            return;
        }

        return data;
    };

    // Call the API
    const submitRequest = async location => {
        setLoading(true);
        setError(false);

        const response = await getWoeid(location);
        if (!response?.woeid) return;
        const data = await getForecastData(response.woeid)
        if (!data) return;

        console.log({data});
    };

    return {
        isError,
        isLoading,
        forecast,
        submitRequest,
    };
};

export default useForecast;
